import React from 'react';
import '../App.css';
import { ListenButton } from './ListenButton';
import './HeroSection.css';
import { ShopButton } from './ShopButton';

function HeroSection() {
  return (
    <div className='hero-container'>
      
      <h1>Erik Burdett</h1>
      <h1></h1>
      <h4>  <i class="fas fa-dharmachakra">  Full Stack Web Developer - Copy Writer - Creative </i> <i class="fas fa-dharmachakra"></i></h4>
      <h5>React - Node - Python - SQL - SQLite - Postgress - CSS - HTML - Javascript - Adobe Suite - Wordpress - Strapi - Headless CMSs - Photography & Video - Writing - Copy Writing - Editing - Publishing  </h5>
      <div className='hero-btns'>
        {/* <Button
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'
        >
          SHOP
        </Button> */}
        <ListenButton
          className='btns'
          buttonStyle='btn--primary'
          buttonSize='btn--large'
          onClick={console.log('hey')}
        >
          CONTACT <i class="fas fa-dharmachakra"/>
        </ListenButton>
        <ShopButton
          className='btns'
          buttonStyle='btn--primary'
          buttonSize='btn--large'
          onClick={console.log('hey')}
        >
          SERVICES <i class="fas fa-dharmachakra"/>
        </ShopButton>
        <ShopButton
          className='btns'
          buttonStyle='btn--primary'
          buttonSize='btn--large'
          onClick={console.log('hey')}
        >
          RESUME <i class="fas fa-dharmachakra"/>
        </ShopButton>
      </div>
    </div>
  );
}

export default HeroSection;
